package mrpio.cookieclicker;import android.util.Log;/** * Created by Valerio on 15/07/2018. */class PowInterpolator implements android.view.animation.Interpolator {    private float mAmplitude = 10;    PowInterpolator(float amplitude) {        mAmplitude = amplitude;    }    public float getInterpolation(float time) {        return (float)((Math.pow (time,mAmplitude)));    }}